#!/usr/bin/python3

#usage 019_PERM.py n

#improt some stuff
from Bio.SeqUtils.ProtParam import ProteinAnalysis
import sys

#get int from command line
prot=str(sys.argv[1])

#analyse the prot
X = ProteinAnalysis(prot)

#print the mw
#The mass of the peptide or protein is the sum of the residue masses plus the mass of water (average mass = 18.0153 Da;)
#but Rosalind don't add the water.
print("%0.3f" % (X.molecular_weight()-18.0153))

#but Bio.SeqUtils.ProtParam uses average mass not monoisotopic mass!!!

#set up dictionary for the monoisotopic masses
dict = {'A': 71.03711,
'R': 156.10111,
'N': 114.04293,
'D': 115.02694,
'C': 103.00919,
'E': 129.04259,
'Q': 128.05858,
'G': 57.02146,
'H': 137.05891,
'I': 113.08406,
'L': 113.08406,
'K': 128.09496,
'M': 131.04049,
'F': 147.06841,
'P': 97.05276,
'O': 237.14773,
'U': 150.95364,
'S': 87.03203,
'T': 101.04768,
'W': 186.07931,
'Y': 163.06333,
'V': 99.06841}

#start with zeor
mw=0

#for each aa in prot
for aa in prot:
   mw+=dict[aa]

#print the mw
print("%0.3f" % mw)

